import{_ as s,c as a,o,a3 as n}from"./chunks/framework.zGi9i9Bf.js";const l="/ProgramLearnNotes/assets/640.DPY3Gmyn.webp",B=JSON.parse('{"title":"Mojo开发环境搭建","description":"","frontmatter":{},"headers":[],"relativePath":"Document/Mojo/Mojo开发环境搭建.md","filePath":"Document/Mojo/Mojo开发环境搭建.md","lastUpdated":1712143193000}'),e={name:"Document/Mojo/Mojo开发环境搭建.md"},p=n('<h1 id="mojo开发环境搭建" tabindex="-1">Mojo开发环境搭建 <a class="header-anchor" href="#mojo开发环境搭建" aria-label="Permalink to &quot;Mojo开发环境搭建&quot;">​</a></h1><h2 id="mojo-安装" tabindex="-1">Mojo 安装 <a class="header-anchor" href="#mojo-安装" aria-label="Permalink to &quot;Mojo 安装&quot;">​</a></h2><p>Linux 与 Mac 环境要求如下， Windows 版本会在未来版本中支持。</p><h3 id="linux" tabindex="-1">Linux: <a class="header-anchor" href="#linux" aria-label="Permalink to &quot;Linux:&quot;">​</a></h3><ul><li>Ubuntu 20.04/22.04 LTS</li><li>x86-64 CPU (with SSE4.2 or newer) or AWS Graviton2/3 CPU</li><li>Minimum 8 GiB RAM</li><li>Python 3.8 - 3.11</li><li>g++ or clang++ C++ compiler</li></ul><h3 id="mac" tabindex="-1">Mac: <a class="header-anchor" href="#mac" aria-label="Permalink to &quot;Mac:&quot;">​</a></h3><ul><li>Apple silicon (M1 or M2 processor)</li><li>macOS Monterey (12) or later</li><li>Python 3.8 - 3.11</li><li>Command-line tools for Xcode, or Xcode</li></ul><p>安装命令：</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">curl</span><span style="color:#D19A66;"> -s</span><span style="color:#98C379;"> https://get.modular.com</span><span style="color:#ABB2BF;"> | </span><span style="color:#61AFEF;">sh</span><span style="color:#98C379;"> -</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>使用以下命令登录 Modular ：</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">modular</span><span style="color:#98C379;"> auth</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>安装 Mojo SDK:</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">modular</span><span style="color:#98C379;"> install</span><span style="color:#98C379;"> mojo</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>安装成功后，使用以下命令设置环境变量：</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#E06C75;">MOJO_PATH</span><span style="color:#56B6C2;">=</span><span style="color:#ABB2BF;">$(</span><span style="color:#61AFEF;">modular</span><span style="color:#98C379;"> config</span><span style="color:#98C379;"> mojo.path</span><span style="color:#ABB2BF;">) </span><span style="color:#56B6C2;">\\ </span><span style="color:#ABB2BF;"> &amp;&amp; </span><span style="color:#E06C75;">BASHRC</span><span style="color:#56B6C2;">=</span><span style="color:#ABB2BF;">$( [ </span><span style="color:#56B6C2;">-f</span><span style="color:#98C379;"> &quot;</span><span style="color:#E06C75;">$HOME</span><span style="color:#98C379;">/.bash_profile&quot;</span><span style="color:#ABB2BF;"> ] &amp;&amp; </span><span style="color:#56B6C2;">echo</span><span style="color:#98C379;"> &quot;</span><span style="color:#E06C75;">$HOME</span><span style="color:#98C379;">/.bash_profile&quot;</span><span style="color:#ABB2BF;"> || </span><span style="color:#56B6C2;">echo</span><span style="color:#98C379;"> &quot;</span><span style="color:#E06C75;">$HOME</span><span style="color:#98C379;">/.bashrc&quot;</span><span style="color:#ABB2BF;"> ) </span><span style="color:#56B6C2;">\\ </span><span style="color:#ABB2BF;"> &amp;&amp; </span><span style="color:#56B6C2;">echo</span><span style="color:#98C379;"> &#39;export MODULAR_HOME=&quot;&#39;</span><span style="color:#E06C75;">$HOME</span><span style="color:#98C379;">&#39;/.modular&quot;&#39;</span><span style="color:#ABB2BF;"> &gt;&gt; </span><span style="color:#98C379;">&quot;</span><span style="color:#E06C75;">$BASHRC</span><span style="color:#98C379;">&quot;</span><span style="color:#56B6C2;"> \\ </span><span style="color:#ABB2BF;"> &amp;&amp; </span><span style="color:#56B6C2;">echo</span><span style="color:#98C379;"> &#39;export PATH=&quot;&#39;</span><span style="color:#E06C75;">$MOJO_PATH</span><span style="color:#98C379;">&#39;/bin:$PATH&quot;&#39;</span><span style="color:#ABB2BF;"> &gt;&gt; </span><span style="color:#98C379;">&quot;</span><span style="color:#E06C75;">$BASHRC</span><span style="color:#98C379;">&quot;</span><span style="color:#56B6C2;"> \\ </span><span style="color:#ABB2BF;"> &amp;&amp; </span><span style="color:#56B6C2;">source</span><span style="color:#98C379;"> &quot;</span><span style="color:#E06C75;">$BASHRC</span><span style="color:#98C379;">&quot;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>接下来就可以使用 mojo 命令进入交互式命令行窗口进行测试了：</p><p><img src="'+l+'" alt="图片"></p>',17),t=[p];function r(c,i,d,u,y,m){return o(),a("div",null,t)}const h=s(e,[["render",r]]);export{B as __pageData,h as default};
