<span class="line"><span style="color:#ABB2BF;">button.</span><span style="color:#61AFEF;">pack</span><span style="color:#ABB2BF;">(</span><span style="color:#E06C75;font-style:italic;">pady</span><span style="color:#56B6C2;">=</span><span style="color:#D19A66;">20</span><span style="color:#ABB2BF;">)</span></span>
