<span class="line"><span style="color:#61AFEF;">    @every</span><span style="color:#ABB2BF;">(</span><span style="color:#E06C75;font-style:italic;">minutes</span><span style="color:#56B6C2;">=</span><span style="color:#D19A66;">24</span><span style="color:#56B6C2;">*</span><span style="color:#D19A66;">60</span><span style="color:#ABB2BF;">)</span></span>
